import logo from './logo.svg';
import axios from "axios";
import './App.css';
import React, {useState, useEffect} from 'react'

function App() {

  let btcCoinbase = {buyPrice : "", sellPrice : ""};
  let btcBinance = {buyPrice : "", sellPrice : ""};
 // let ethCoinbase = {buyPrice : "", sellPrice : ""};
  let ethBinance = {buyPrice : "", sellPrice : ""};

  const [ethCoinbaseBuy, setEthCoinbaseBuy] = useState(null)
  const [ethCoinbaseSell, setEthCoinbaseSell] = useState(null)

  useEffect(() => {
    axios.get("https://api.coinbase.com/v2/prices/ETH-USD/buy")
    .then((response)=>{
      setEthCoinbaseBuy(response.data.data.amount)
    })
  }, [])

  useEffect(() => {
    axios.get("https://api.coinbase.com/v2/prices/ETH-USD/sell")
    .then((response)=>{
      setEthCoinbaseSell(response.data.data.amount)
    })
  }, [])

  // useEffect(() => {
  //   axios.get("https://api.coinbase.com/v2/prices/ETH-USD/sell")
  //   .then((response)=>{
  //     ethCoinbase.sellPrice = response.data.data.amount;
  //     // axios returns API response body in .data
  //   })
  // }, [])

  axios
      .get(
     "https://api.coinbase.com/v2/prices/BTC-USD/buy"
      )
      .then((res) => {
        btcCoinbase.buyPrice = res.data.data.amount;
      })
      .catch((error) => console.error(error));

      axios
      .get(
     "https://api.coinbase.com/v2/prices/BTC-USD/sell"
      )
      .then((res) => {
        btcCoinbase.sellPrice = res.data.data.amount;
      })
      .catch((error) => console.error(error));

  axios
      .get(
       "https://api.binance.com/api/v3/ticker/bookTicker?symbol=BTCUSDT"
      )
      .then((res) => {
        btcBinance.buyPrice = res.data.askPrice;
        console.log(btcBinance.buyPrice)
      })
      .catch((error) => console.error(error));

      console.log("Now: ")
        console.log(btcBinance.buyPrice)

      axios
      .get(
       "https://api.binance.com/api/v3/ticker/bookTicker?symbol=BTCUSDT"
      )
      .then((res) => {
        btcBinance.sellPrice = res.data.bidPrice;
      })
      .catch((error) => console.error(error));

      axios
      .get(
       "https://api.binance.com/api/v3/ticker/bookTicker?symbol=ETHUSDT"
      )
      .then((res) => {
        ethBinance.buyPrice = res.data.askPrice;
      })
      .catch((error) => console.error(error));

      axios
      .get(
       "https://api.binance.com/api/v3/ticker/bookTicker?symbol=ETHUSDT"
      )
      .then((res) => {
        ethBinance.sellPrice = res.data.bidPrice;
      })
      .catch((error) => console.error(error));

 // console.log("ETH: "+ethCoinbase.buyPrice+" and "+ethCoinbase.sellPrice)

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} className="App-logo" alt="logo" />
        <h2>Binance: </h2>
        <p>Bitcoin (BTC): Buy Price = {btcBinance.buyPrice}, Sell Price = {btcBinance.sellPrice}</p>
        <a
          className="App-link"
          href="https://reactjs.org"
          target="_blank"
          rel="noopener noreferrer"
        >
          Learn React
        </a>
      </header>
    </div>
  );
}

export default App;
